    return(
        <>
            <div className="container">
                {/* -------------------------------------------- Login Page ------------------------------------- */}
            <div id='LoginPage' className='loginPage'>
                <h3>Login</h3>
                <label>Email</label>
                <input type="text" placeholder="Enter Email" name="email" required onChange={(e) => setEmail(e.target.value)}></input>

                <label >Password</label>
                <input type="password" placeholder="Enter Password" name="psw" required onChange={(e) => setPassWord(e.target.value)}></input>
                    
                <button type="submit" onClick={onLoginClick}>Login</button>
                <div className="forgot-links">
                    <a className="left-link" href='/' onClick={passwordLinkClick}>Forgot Your Password</a>
                    <a className="right-link" href='/' onClick={emailLinkClick}>Forgot Your Name</a>
                </div>
            </div>
            </div>
        </>
    );
}
export default Login;
