import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FadeLoader } from 'react-spinners';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './ServiceRequestTab.css';
function ServiceRequestTab() {

    const pythonDomain = 'http://localhost:5000/';

    const [serviceList, setServiceList] = useState([]);
    const [modal, setModal] = useState(false);
    const [loading, setLoading] = useState(false);

    const [id, setServiceId] = useState();
    const [name, setName] = useState();
    const [title, setTitle] = useState();
    const [status, setStatus] = useState();
    const [description, setDescription] = useState();


    useEffect(() => {
        axios.post(`${pythonDomain}serviceRequests`)
            .then(response => {
                setServiceList(response.data);
            })
            .catch(error => {
                console.error('Error fetching record details:', error);
            });
    }, []);


    const viewRequests = (reqId) => {
        setLoading(true);
        setModal(true);
        axios.post(`${pythonDomain}updateServiceRecord?serviceRecordId=${reqId}`)
        .then(response => {
            console.log('Service Request Details:: ',response.data.requestRecord.Id)
            let reqRec = response.data.requestRecord;
                setServiceId(reqRec.Id);
                setName(reqRec.Name);
                setStatus(reqRec.Request_Status__c);
                setTitle(reqRec.Request_Title__c);
                setDescription(reqRec.Description__c);
                setLoading(false);
        }) 
        .catch(error => { 
            console.error('AwardDetails Error fetching data:', error); 
        });
    }

    const onSave = () => {
        const RequestRecordData =
            { id: id, name: name, title: title, status: status, description: description } 
       
        console.log('RequestRecordData:: ',JSON.stringify(RequestRecordData))
        axios.post(`${pythonDomain}newExtraRecord?extraRecord=${JSON.stringify(RequestRecordData)}`)
            .then(response => {
                if (response.data === 'Success') {
                    toast.success(response.data);
                    axios.post(`${pythonDomain}serviceRequests`)
                        .then(response => {
                            setServiceList(response.data);
                            setModal(false);
                            setName('');
                            setTitle('');
                            setStatus('');
                            setDescription('');
                        })
                        .catch(error => {
                            console.error('Error fetching award details:', error);
                        });
                } else {
                    toast.error(response.data);
                }
            })
            .catch(error => {
                console.error('Error saving award:', error);
            });
    }

    const onModalClose = () => {
        setModal(false)
    }

  return (
    <div>
    <div>
    <center>
    <div className='PageTitle'>ServiceRequestTab</div>
    {loading && (
                <div className="spinner-container">
                    <FadeLoader color={'#000000'} loading={loading} size={100} />
                </div>
            )}
    <div className='FamilyTable' style={{width:'900px'}}>
    <table>
        <thead>
            <tr>
                <th style={{ width: '20%', textAlign: 'left' }}>Name</th>
                <th style={{ width: '25%', textAlign: 'left' }}>Request Open Date Time</th>
                <th style={{ width: '20%', textAlign: 'left' }}>Request Status</th>
                <th style={{ width: '20%', textAlign: 'left' }}>Request Sources</th>
                <th style={{ width: '20%', textAlign: 'left' }}>Action</th>
            </tr>
        </thead>
        <tbody>
            
        {serviceList.map((ser) => (
                            <tr key={ser.Id}>
                                <td>{ser.Name}</td>
                                <td>{ser.Request_Open_Date_Time__c}</td>
                                <td>{ser.Request_Status__c}</td>
                                <td>{ser.Request_Source__c}</td>
                                <td>
            <div>
                <button type='button' className='buttonStyle' onClick={() => viewRequests(ser.Id)} style={{ marginRight: '10px' }}>
                     View
                </button>
                </div>
            </td>
            </tr>
            ))}
        </tbody>
    </table>
    </div>
    </center>
    </div>

    {modal && (
        <div className='newFamily'>
            <div className="modal-overlay">
            <ToastContainer />
                <div className="modal">
                    <div className="modal-header">
                        <button className="close-button" onClick={onModalClose}>&times;</button>
                        <h3>Service Request Details</h3>
                    </div>
                    <div className="modal-body">
                        <form>
                            <div className="account-details">
                                <div className="custom-select">
                                    <label htmlFor="Number">Request Id</label>
                                    <input type="text"  name="RequestId" value={name} onChange={(e) => setName(e.target.value)}/>
                                </div>
                                
                                <div className="custom-select">
                                    <label htmlFor="Status">Status</label>
                                    <input type="text"  name="Status" value={status} onChange={(e) => setStatus(e.target.value)}/>
                                </div>
                                <div className="custom-select">
                                    <label htmlFor="Query">Query</label>
                                    <textarea type="text"  name="Query" value={title} onChange={(e) => setTitle(e.target.value)}/>
                                </div>
                                <div className="custom-select">
                                    <label htmlFor="Description">Description</label>
                                    <textarea style={{overflow:'scroll'}} type="text"  name="Description" value={description} onChange={(e) => setDescription(e.target.value)}/>
                                </div>
                            </div>
                        </form>
                    </div>
                    <div className="modal-footer">
                        <button className="buttonStyle" style={{ marginRight: '10px' }} onClick={onModalClose}>Close</button>
                    </div>
                </div>
            </div>
        </div>
    )}
    <center><div className='buttonsDiv'><button type='button' className='buttonStyle' onClick={onSave}>New Request</button></div></center>
    </div>
  )
}

export default ServiceRequestTab
