import React from 'react';
import { useState, useEffect } from "react";
import axios from 'axios';
import './Awards.css';
import { FadeLoader  } from 'react-spinners';
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';



function Awards(){
   

    const pythonDomain = 'http://localhost:5000/';

    const [awardsList, setawardsList] = useState([]);
    const [modal, setModal] = useState(false);
    const [loading, setLoading] = useState(false);

    const [typeOptions, settypeOptions] = useState(useState(new Map()));
    

    const[title,setTitle] = useState('');
    const[type,setType] = useState('');
    const[date,setDate] = useState(''); 
    const[description,setDescription] = useState('');
    
    


    useEffect(() => {
        // if(educationList === undefined){
            axios.post(`${pythonDomain}awardDetails`)
            .then(response => {
                setawardsList(response.data);
                console.log('AwardsDetails:: ',response.data)
            }) 
            .catch(error => { 
                console.error('AwardsDetails Error fetching data:', error); 
            });
        //}
    }, []);

    
    const addNewFamily = () =>{

        // setLoading(true)
        axios.post(`${pythonDomain}awardsPickLists`)
        .then(response => {
            const noneOption = { id: null, name: '--None--' };
            const typeOptions = [noneOption, ...Object.entries(response.data.typePickList).map(([key, value]) => ({ id: key, name: value }))];              
            settypeOptions(typeOptions);
            
            
            if(typeOptions !== undefined){
                setLoading(false)
                setModal(true)
            }
        }) 
        .catch(error => { 
            console.error('Error fetching data:', error); 
        });
    }
    
    
    const updateFamily = () =>{
        
    }
    const deleteFamily = () =>{

    }
    const onSave =() =>{

        const awardsRecordData = {title:title,type:type,date:date,description:description}

        console.log('awardsRecordData:: ',JSON.stringify(awardsRecordData))   
        
        axios.post(`${pythonDomain}newAwardRecord?AwardRecord=${JSON.stringify(awardsRecordData)}`)
        .then(response => {
            console.log('awardData:: ',response.data)
            if(response.data === 'Success'){
                toast.success(response.data, {
                    position: "top-center",
                    autoClose: 5000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                    theme: "colored",
                  });
                setModal(false);
                setTitle('');
                setType('');
                setDate('');
                setDescription('');
                
            }else{
                toast.error(response.data, {
                    position: "top-center",
                    autoClose: 5000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                    theme: "colored",
                  });
            }
            
        }) 
        .catch(error => { 
            console.error('AwardData Error fetching data:', error); 
        }); 

    }
    const onModalClose =() =>{
        setModal(false);
        setTitle('');
        setType('');
        setDate('');
        setDescription('');
    }

       
           

    return (
        <div>
            <div className='PageTitle'>Awards</div>
            {loading && (
                        <div className="spinner-container">
                            <FadeLoader  color={'#000000'} loading={loading} size={100} />
                        </div>
                    )}
            
            <div className='FamilyTable'>
                <table style={{width:'100%',paddingTop:'10px'}}>
                <thead>
                 <tr>
                    <th style={{ width: '25%', textAlign: 'left' }}>Title</th>
                    <th style={{ width: '25%', textAlign: 'left' }}>Type</th>
                    <th style={{ width: '25%', textAlign: 'left' }}>Date</th>
                    <th style={{ width: '25%', textAlign: 'left' }}></th>
                </tr>
                </thead>

                    <tbody>  
                    {awardsList.map((aws) => (
                            <tr key={aws.Id}>
                                <td>{aws.Name}</td>
                                <td>{aws.Type__c}</td>
                                <td>{aws.Date__c}</td>
                                <td>
                                    <button type='submit' className='buttonStyle' onClick={updateFamily} style={{ marginRight: '10px' }}>
                                        Update
                                    </button>
                                    <button type='submit' className='buttonStyle' onClick={deleteFamily} style={{ backgroundColor:'#ba0517' }}>
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
            {modal && (
                <div className='newFamily'>
                    <div className="modal-overlay">
                    <ToastContainer/>
                        <div className="modal">
                            <div className="modal-header">
                                <button className="close-button" onClick={onModalClose}>
                                    &times;        
                                </button>
                                <h3>New Award</h3>
                            </div>
                            <div className="modal-body">
                                <form>
                                    <div className="account-details">
                                        <div className="custom-select">
                                            <label htmlFor="ApplicationNo">Title</label>
                                            <input type="text" required name="title" value={title}  onChange={(e) => setTitle(e.target.value)}></input>
                                        </div>
                                        <div className="custom-select">
                                            <label htmlFor="SubmittedDate">Type</label>
                                            <select className='input-name' name="type" value={type}  required onChange={(e) => setType(e.target.value)}>
                                            {typeOptions.map(option => (
                                                    <option key={option.id} value={option.id}>
                                                        {option.name}
                                                    </option>
                                                ))}
                                            </select>
                                        </div>
                                        <div className="custom-select">
                                            <label htmlFor="SubmittedDate">Date</label>
                                            <input type="date" required name="date" value={date}  onChange={(e) => setDate(e.target.value)}></input>
                                        </div>
                                        <div className="custom-select">
                                            <label htmlFor="SubmittedDate">Description</label>
                                            <textarea type="text" name="description" value={description}  onChange={(e) => setDescription(e.target.value)}></textarea>
                                        </div>
                                    </div>
                                </form>
                            </div>
                            <div className="modal-footer">
                                <button className="buttonStyle" style={{ marginRight: '10px' }} onClick={onModalClose}>Cancel</button>
                                <button className="buttonStyle" onClick={onSave}>Save</button>
                            </div>
                        </div>
                    </div>
                </div>
            )}
                <div className='buttonsDiv'><button type='submit' onClick={addNewFamily} className='buttonStyle'><i className="fa fa-plus"></i>Add New</button></div>
                <div className='continueButton'><button type='submit' className='buttonStyle'>Continue</button></div>
            </div>
       
    );
}


export default Awards;
