import React, { useState } from 'react';
import './login.css';
const Login = () => {
  const [email, setEmail] = useState('');

  const [dataInput, setDataInput] = useState('');
  const submitThis = () => {
    const info = { email: email};
    fetch('/login', {
      method:'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(info),

    })

      .then((response) => response.json())
      .then((data) => {
        setDataInput(data.code);

        if(data.code === 'Successful')
        {
          window.location.href = '/home';
          window.open();
        }
      });
  };

  return (
    <div className="login-container">
      <form>
        <div className="login-input">
          <label className="input-label" htmlFor="email"></label>
          <input className="user-input"
            type="text"
            name="email"
            id="email"
            placeholder= "Enter your Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="login-button">
          <button className='click-button'  type="button" onClick={submitThis}>
            Login
          </button>
        </div>
        <div>
        <p><a className="forgot-password-link" href="/">Forgot Password?</a>&nbsp; &nbsp;<a className="forgot-password-link" href="/register">Create an account</a></p>
        </div>
      </form>
      <div>{dataInput}</div>
    </div>
  );
};

export default Login;
