import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function Login() {
  const pythonDomain = 'http://localhost:5000';
  const [email, setEmail] = useState('');
  const [passWord, setPassWord] = useState('');

  const onLoginClick = () => {
    console.log('Email: ', email, ' Password: ', passWord);

    axios.post(`${pythonDomain}/login`, {
      email: email,
      passWord: passWord,
    })
      .then(response => {
        console.log('Success Response:', response.data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
      });
  }

  const passwordLinkClick = () => {
    // Handle password link click
  }

  const emailLinkClick = () => {
    // Handle email link click
  }

  return (
    <div className="container">
      <div id='LoginPage' className='loginPage'>
        <h3>Login</h3>
        <label>Email</label>
        <input type="text" placeholder="Enter Email" name="email" required onChange={(e) => setEmail(e.target.value)}></input>

        <label>Password</label>
        <input type="password" placeholder="Enter Password" name="psw" required onChange={(e) => setPassWord(e.target.value)}></input>

        <button type="button" onClick={onLoginClick}>Login</button>
        <div className="forgot-links">
          <a className="left-link" href='/' onClick={passwordLinkClick}>Forgot Your Password</a>
          <a className="right-link" href='/' onClick={emailLinkClick}>Forgot Your Name</a>
        </div>
      </div>
    </div>
  );
}

export default Login;
