import React, { useState } from 'react';
import axios from 'axios';
import './App.css';


function Authentication(){

    const pythonDomailn = 'http://localhost:5000/';

    const [currentPage, setCurrentPage] = useState('login');
 
    const [userName, setUserName] = useState('');
    const [passWord, setPassWord] = useState('');
    const [email, setEmail] = useState('');
    const [newPassword, setnewPassword] = useState('');
    const [confirmNewPassword, setconfirmNewPassword] = useState('');

    const[userNameError, setuserNameError] = useState(false);
    const[passwordError, setpasswordError] = useState(false);

    const [invUserName, setInvUserName] = useState(false);
    const [passwordMatch, setPasswordMatch] = useState(false);

    const onLoginClick = (e) =>{
      e.preventDefault();
        console.log('UserName: ',userName, ' PassWord: ',passWord);
        if(userName !== '' && passWord !== ''){
          axios.post(`${pythonDomailn}login`, {
            userName: userName,
            password: passWord
         })
            .then(response => {
                console.log('Login Response:: ',response.data)
                if(response.data === 'invalid Username'){
                    setuserNameError(true);
                }else if(response.data === 'invalid Password'){
                    setpasswordError(true);
                }else{
                    console.log('Login Success');
                }
            }) 
            .catch(error => { 
                console.error(' Login Failed Error', error); 
            });
        }
    }

    const passwordLinkClick = () =>{
        console.log('passwordLinkClick');
    }

    const usernameLinkClick = () =>{
        console.log('usernameLinkClick');
    }

    const onSignUpLinkClick = () =>{
        setCurrentPage('signup');
    }
    const onContinue = (e) =>{
      e.preventDefault();
        console.log('email: ',email, ' newPassword: ',newPassword, ' confirmNewPassword: ',confirmNewPassword);
        if(email !== '' && newPassword !== '' && confirmNewPassword !==''){
          if (newPassword === confirmNewPassword) {
          axios.post(`${pythonDomailn}register`, { email, newPassword, confirmNewPassword })
          .then(response => {
             console.log('Registration Response:', response.data);
             if(response.data === 'invalid Username'){
                setInvUserName(true);
             }else{

             }
          })
          .catch(error => {
             console.error('Error during registration:', error);
          });
        } else {
          console.error('New Password and Confirm Password do not match.');
          setPasswordMatch(true);
       }
       
        }
    }
    const onCancel = () =>{
        setCurrentPage('login');
    }

    return(
        <>
            <div className="container">
                {/* -------------------------------------------- Login Page ------------------------------------- */}
                <div id={currentPage === 'login' ? 'LoginPage' : 'SignupPage'} className={currentPage === 'login' ? 'loginPage' : 'signupPage'}>
                    {currentPage === 'login' ? (
                        <>
                            <h3>Login</h3>
                            <form>
                                <label>Username</label>
                                <input type="text" placeholder="Enter Username" name="uname" required onChange={(e) => setUserName(e.target.value)}></input>
                                {userNameError && <div>invalid Username</div>}

                                <label >Password</label>
                                <input type="password" placeholder="Enter Password" name="psw" required onChange={(e) => setPassWord(e.target.value)}></input>
                                    
                                <button className='loginbutton' type="submit" onClick={onLoginClick}>Login</button>
                            </form>
                            
                            <div className="forgot-links">
                                <button className="left-link" onClick={passwordLinkClick}>Forgot Your Password</button>
                                <button className="right-link" onClick={usernameLinkClick}>Forgot Your Username</button>
                            </div>
                            <div className='signUpDiv'>
                                Don't have an account? <a href='#' onClick={onSignUpLinkClick}>Sign Up</a>
                            </div>
                        </>
                    ):(
                        <>
                        {/* -------------------------------------------- SignUp Page ------------------------------------- */}
                            <h3>Set Password</h3>
                            <label>Enter your Email</label>
                            <input type="text" placeholder="Enter Username" name="uname" required onChange={(e) => setEmail(e.target.value)}></input>

                            <label >New PassWord</label>
                            <input type="password" placeholder="Enter Password" name="psw" required onChange={(e) => setnewPassword(e.target.value)}></input>

                            <label >Confirm New Password</label>
                            <input type="password" placeholder="Enter Password" name="psw" required onChange={(e) => setconfirmNewPassword(e.target.value)}></input>

                            <button className='' type="submit" onClick={onCancel}>Cancel</button>
                            <button className='' type="submit" onClick={onContinue}>Continue</button>
                        </>
                    )}
                </div>
            </div>
        </>
    );
}
export default Authentication;
